import { observer } from 'mobx-react-lite';
import React, { useContext, useEffect, useState } from 'react';
import { Button, Form, Spinner } from 'react-bootstrap';
import Modal from "react-bootstrap/Modal";
import { Context } from '../..';


const Rating = observer(({ show, onHide, ratedDeviceId}) => {
    const [value, setValue] = useState ('')
    const {device} = useContext(Context)
    const [ratedDevice, setRatedDevice] = useState('')
    // const ratedDevice=(device.devices.map(device => device.id === ratedDeviceId ? device : ''))
    const [loading, setLoading] = useState(true)

    useEffect( () => {
        setRatedDevice(device.devices.map(device => device.id === ratedDeviceId ? device : ''))
        setLoading(false)
    }, [])

    if (loading) {
        return <Spinner animation={"grow"}/>
      }
    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            Добавить новый брэнд {ratedDevice}
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
                <Form.Control
                    value = {value}
                    onChange = {e => setValue(e.target.value)}  
                    placeholder={"Введите название типа"}
                />
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
            <Button variant="outline-success" onClick={onHide}>Добавить</Button>
        </Modal.Footer>
        </Modal>
    );
});

export default Rating;
