import { observer } from 'mobx-react-lite';
import React, { useContext, useEffect, useState } from 'react';
import { Container, Image, Row, Spinner, Table } from 'react-bootstrap';
import { Context } from '..';
import { addToBasket, checkBasket, getBasket } from '../http/basketAPI';
import greenCheck from '../assets/greenCheck.png';
import greenPlus from '../assets/greenPlus.png';
import redminus from '../assets/redminus.png';

const Basket = observer (() => {
    const {device} = useContext(Context)
    const [loading, setLoading] = useState(true)
    useEffect(() => {
        getBasket()
        .then(data => device.setBasket(data))
        .finally(setLoading(false))

    }, [])

    const clickAdd = async(deviceId) => {
        try {
            let data;
            data = await addToBasket(deviceId)
            const check = await checkBasket()
            device.setCountBasket(check)
            alert(data)
        } catch(e) {
            alert(e.response.data.message)
        }
    }




    if (loading) {
        return <Spinner animation={"grow"}/>
        }
    return (

        <Container>
            {(device.countBasket===0)
            ?
            <h1 style={{ marginTop:"130px", textAlign:'center'}}>ВАША КОРЗИНА ПУСТА</h1>
            :
            <div>
                <h1 style={{ marginTop:"130px", textAlign:'center'}}>В ВАШЕЙ КОРЗИНЕ {device.countBasket} ДЕВАЙСОВ</h1>
                <Table striped bordered hover className='align-middle'>
                    <thead>
                        <tr>
                            <th></th>
                            <th>Тип девайса</th>
                            <th>Бренд</th>
                            <th>Модель</th>
                            <th>Изображение</th>
                            <th>Количество</th>
                            <th>Цена</th>
                            <th>Сумма</th>
                            <th>+</th>
                            <th>-</th>
                        </tr>
                    </thead>
                    <tbody>

                            {device.basket.map((device,number) =>
                                <tr key={number}>
                                <td>{number+1}</td> 
                                <td>{device["basket_devices.device.type.typename"]}</td>
                                <td>{device["basket_devices.device.brand.brandname"]}</td>
                                <td>{device.name}</td>
                                <td><Image width={100} height={100}  src={process.env.REACT_APP_API_URL + device.img} /></td>
                                <td>{device.count}</td>
                                <td>{device.price}</td>
                                <td>{device.price * device.count}</td>
                                <td><div
                                    className="d-flex align-items-center justify-content-center"
                                    style={{background: `url(${greenPlus}) no-repeat center center`, cursor:'pointer', width:30, height:30, display:"table-cell",backgroundSize: 'cover', fontSize:64}}
                                    onClick={() => {
                                        clickAdd(device["basket_devices.deviceId"])
                                    }} 
                                    /></td>
                                <td><div
                                    className="d-flex align-items-center justify-content-center"
                                    style={{background: `url(${redminus}) no-repeat center center`,  cursor:'pointer', width:30, height:30, display:"table-cell",backgroundSize: 'cover', fontSize:64}}
                                    /></td>
                                </tr>
                            )}

                    </tbody>
                </Table>

            </div>


            }






        </Container>


    );
});

export default Basket;
