import { observer } from 'mobx-react-lite';
import React, { startTransition, useContext, useEffect, useState } from 'react';
import { Button, Spinner } from 'react-bootstrap';
import Modal from "react-bootstrap/Modal";
import { Context } from '../..';
import '../../assets/stars.css'


const Rating = observer(({ show, onHide, ratedDeviceId}) => {
    const {device} = useContext(Context)
    const [ratedDevice, setRatedDevice] = useState('')


    useEffect( () => {
        device.devices.map(device => device.id === ratedDeviceId ? setRatedDevice(device) : '')
    }, [ratedDeviceId])

    useEffect(() => {
        const script = document.createElement('script');

        script.src = "https://kit.fontawesome.com/5ea815c1d0.js";
        script.async = true;

        document.body.appendChild(script);

        return () => {
          document.body.removeChild(script);
        }
      }, []);

    if(!ratedDevice){
        return <div></div>
    }

    const rates = []
    // for (var i = 10; i > 0; i--){console.log(i)}
    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            Поставьте Вашу оценку девайсу {ratedDevice.brand.name} {ratedDevice.name}
            </Modal.Title>
        </Modal.Header>
        <Modal.Body style={{background: "#353b48", padding: "4em"}}>
            <div className="star-wrapper">
                for (var i = 10; i > 0; i--){console.log(i)}
                <a href="#" className="fas fa-star s10"></a>
                <a href="#" class="fas fa-star s1"></a>
                <a href="#" class="fas fa-star s2"></a>
                <a href="#" class="fas fa-star s3"></a>
                <a href="#" class="fas fa-star s4"></a>
                <a href="#" class="fas fa-star s5"></a>
                <a href="#" class="fas fa-star s6"></a>
                <a href="#" class="fas fa-star s7"></a>
                <a href="#" class="fas fa-star s8"></a>
                <a href="#" class="fas fa-star s9"></a>

            </div>
        </Modal.Body>
        <Modal.Footer>
            <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
            <Button variant="outline-success" onClick={onHide}>Добавить</Button>
        </Modal.Footer>
        </Modal>
    );
});

export default Rating;
