import React from 'react';
import { Button, Card, Col, Container, Image, Row } from 'react-bootstrap';
import deviceStar from '../assets/deviceStar.png'

const DevicePage = () => {
    const device = { id: 1, name: "12 pro", price: 120000, rating: 3.3, img: "d49ee446-ce2a-46f5-9c70-0c92c0cfe166.jpg"}
    const description = [
        {id:1, title: "Оперативная память", description: '5ГБ'},
        {id:2, title: "Оперативная память", description: '5ГБ'},
        {id:3, title: "Оперативная память", description: '5ГБ'},
        {id:4, title: "Оперативная память", description: '5ГБ'},
        {id:5, title: "Оперативная память", description: '5ГБ'}
    ]
    return (
        <Container>
            <Row>
                <Col md={4}>
                    <Image width={300} height={300} src ={device.img}/>
                </Col>
                <Col md={4}>
                    <div className='d-flex flex-column align-items-center'>
                        <h2>{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{background: `url(${deviceStar}) no-repeat center center`, width:240, height:240, backgroundSize: 'cover', fontSize:64}}
                        >
                            {device.rating}
                        </div>
                    </div>
                </Col>  
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{width: 300, height: 300, fontSize:32, border: '5px solid lightgrey'}}
                    >
                        <h3>От: {device.price}</h3>
                        <Button variant={'outline-dark'}>Добавить в корзину</Button>
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1>Характеристики</h1>
                {description.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10 }}>
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>      
        </Container>
    );
};

export default DevicePage;
