import { observer } from 'mobx-react-lite';
import React, { useContext, useEffect, useState } from 'react';
import { Container, Row, Spinner } from 'react-bootstrap';
import { Context } from '..';
import { getBasket } from '../http/basketAPI';

const Basket = observer (() => {
    const {device} = useContext(Context)
    const [loading, setLoading] = useState(true)
    useEffect(() => {
        getBasket()
        .then(data => device.setBasket(data))
        .finally(setLoading(false))

    }, [])




    if (loading) {
        return <Spinner animation={"grow"}/>
        }
    return (

        <Container>
            {(device.countBasket===0)
            ?
            <h1 style={{ marginTop:"130px", textAlign:'center'}}>ВАША КОРЗИНА ПУСТА</h1>
            :
            <div>
                <h1 style={{ marginTop:"130px", textAlign:'center'}}>В ВАШЕЙ КОРЗИНЕ {device.countBasket} ДЕВАЙСОВ</h1>
                {device.basket.map(device => 
                    <div key={device['basket_devices.deviceId']}>
                        {device["basket_devices.device.type.typename"]} {device["basket_devices.device.brand.brandname"]} {device.name} 
                        {device.price} 
                        {device.rating}
                        {device.count} 
                        {device.img}
                        </div>)}
            </div>


            }






        </Container>


    );
});

export default Basket;
