import { observer } from 'mobx-react-lite';
import React, { useContext, useEffect, useState } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { Context } from '..';
import BrandBar from '../components/BrandBar';
import DeviceList from '../components/DeviceList';
import Rating from '../components/modals/Rating';
import Pages from '../components/Pages';
import TypeBar from '../components/TypeBar';
import { fetchBrands, fetchDevices, fetchTypes } from '../http/deviceAPI';

const Shop = observer (() => {
    const {device} = useContext(Context)
    const [ratingVisble, setRatingVisible] = useState(false)
    const [ratedDevice, setRatedDevice] = useState('')


    useEffect(() => {
        fetchTypes().then(data => device.setTypes(data))
        fetchBrands().then(data => device.setBrands(data))
        fetchDevices(null, null, 1, 3).then(data => {
            device.setDevices(data.rows)
            device.setTotalCount(data.count)
            device.setSelectedType({})
            device.setSelectedBrand({})
        })
    }, [])

    useEffect( () => {
        fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, device.limit).then(data => {
            device.setDevices(data.rows)
            device.setTotalCount(data.count)
        })
    }, [device.page, device.selectedType, device.selectedBrand])

    return (
        <Container>
            <Row className="mt-2"> 
                <Col md={3}>
                    <TypeBar/>
                    {ratedDevice}
                </Col>
                <Col md={9}>
                    <BrandBar/>
                    <DeviceList showRating={() => setRatingVisible(true)} setRatedDevice={(id)=> setRatedDevice(id)}/>
                    <Pages/>
                    <Rating show={ratingVisble} onHide={() => setRatingVisible(false)} />
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
