import React from 'react';
import { Col, Container, Image, Row } from 'react-bootstrap';
import deviceStar from '../assets/deviceStar.png'

const DevicePage = () => {
    const device = { id: 1, name: "12 pro", price: 120000, rating: 3, img: "d49ee446-ce2a-46f5-9c70-0c92c0cfe166.jpg"}
    return (
        <Container>
            <Col md={4}>
                <Image width={300} height={300} src ={device.imd}/>
            </Col>
            <Col md={4}>
                <Row>
                    <h2>{device.name}</h2>
                    <div
                        className="d-flex align-items-center justify-content-center"
                        style={{background: `url(${deviceStar}) no-repeat center center`, width:240, height:240, backgroundSize: 'cover'}}
                    >
                        {device.rating}
                    </div>
                </Row>
            </Col>  
            <Col md={4}>

            </Col>      
        </Container>
    );
};

export default DevicePage;
